    private void botonPActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonPActionPerformed
    double num1 = Double.parseDouble(txtF1.getText());
    double num2 = Double.parseDouble(txtF2.getText());
    double num3 = Double.parseDouble(txtF3.getText());
    
    double NumMayor;
  
    if (num1>num2 && num1>num3){
    NumMayor= num1;
    txtArea.setText("El primer numero es el mayor: "+NumMayor);}
    else if(num2>num1 && num2>num3){
    NumMayor= num2;
    txtArea.setText("El segundo numero es mayor: "+NumMayor);}
    else if(num3>num1 && num3>num2){
    NumMayor=num3;
    txtArea.setText("El tercer numero es mayor: "+NumMayor);}
    else{
    txtArea.setText("Valores no validos");}
       
    
    
    
    }//GEN-LAST:event_botonPActionPerformed

